import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

const Navbar = () => {
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);

  // Close mobile menu on route change for better UX
  useEffect(() => {
    setMenuOpen(false);
  }, [location.pathname]);

  const isActive = (path) =>
    path === ""
      ? location.pathname === "/" ||
        location.pathname === "/user/dashboard/home"
      : location.pathname.includes(path);

  return (
    <>
      <style>{`
        /* Rainbow text animation */
        .rainbow-text {
          background: linear-gradient(90deg, #ff0057, #ff7c00, #ffeb00, #47ff00, #00ffee, #2b65ff, #8000ff);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          animation: rainbow 5s linear infinite;
          background-size: 400%;
        }
        @keyframes rainbow {
          0% { background-position: 0%; }
          100% { background-position: 400%; }
        }
        /* Active link highlighting */
        .active-link {
          color: #00d8ff;
          font-weight: bold;
          border-bottom: 2px solid #00d8ff;
        }
      `}</style>

      <nav className="fixed top-0 left-0 w-full bg-black bg-opacity-90 z-50 shadow-md">
        <div className="max-w-7xl mx-auto flex justify-between items-center py-4 px-6 md:px-10 h-16 md:h-20">
          {/* Logo */}
          <h2 className="text-3xl md:text-4xl font-extrabold rainbow-text select-none tracking-wide whitespace-nowrap">
            BLOGGING PLATFORM
          </h2>

          {/* Hamburger Button for Mobile */}
          <button
            className="text-3xl text-white md:hidden focus:outline-none"
            onClick={() => setMenuOpen(!menuOpen)}
            aria-label="Toggle Menu"
          >
            {menuOpen ? "✕" : "☰"}
          </button>

          {/* Menu Links */}
          <ul
            className={`
              fixed top-16 left-0 w-full bg-black bg-opacity-90 shadow-md
              md:static md:flex md:items-center md:bg-transparent md:shadow-none
              md:w-auto
              transition-max-height duration-300 ease-in-out overflow-hidden
              ${menuOpen ? "max-h-screen py-4" : "max-h-0 py-0"}
              md:max-h-full md:py-0
              flex flex-col md:flex-row
              gap-4 md:gap-8
              text-white
              font-medium
            `}
            style={{ zIndex: 40 }}
          >
            {[
              { to: "home", label: "Home" },
              { to: "create-post", label: "Create Blogs" },
              { to: "my-posts", label: "My Blogs" },
              { to: "edit-posts", label: "Edit Blogs" },
              { to: "profile", label: "Profile" },
              { to: "contact", label: "Contact" },
              { to: "about", label: "About" },
            ].map(({ to, label }) => (
              <li key={label}>
                <Link
                  to={`/user/dashboard/${to}`}
                  className={`block px-4 py-2 rounded
                    hover:text-blue-400 hover:bg-gray-800
                    transition-colors duration-200
                    ${isActive(to) ? "active-link" : ""}
                  `}
                  onClick={() => setMenuOpen(false)} // Close menu on link click (mobile)
                >
                  {label}
                </Link>
              </li>
            ))}

            <li>
              <Link
                to="logout"
                className="block px-4 py-2 bg-red-600 rounded text-white hover:bg-red-700 transition-colors duration-200"
                onClick={() => setMenuOpen(false)}
              >
                Logout
              </Link>
            </li>
          </ul>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
