import React, { useState } from "react";
import { Link, Outlet, useNavigate, useLocation } from "react-router-dom";

const UserDashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const user = JSON.parse(localStorage.getItem("user"));
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  // Helper to check if a route is active
  const isActive = (path) =>
    path === ""
      ? location.pathname === "/" || location.pathname === "/dashboard"
      : location.pathname.includes(path);

  return (
    <>
      <style>{`
        /* Navbar fixed and overlay fix */
        nav {
          position: relative;
          z-index: 20;
          background-color: rgba(0, 0, 0, 0.85);
        }

        /* Rainbow gradient animated text */
        @keyframes rainbow {
          0%, 100% {
            background-position: 0% 50%;
          }
          50% {
            background-position: 100% 50%;
          }
        }
        .rainbow-text {
          background: linear-gradient(270deg, #ff0000, #ffa500, #ffff00, #00ff00, #00ffff, #0000ff, #ff00ff, #ff0000);
          background-size: 1600% 1600%;
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          animation: rainbow 8s ease infinite;
          user-select: none;
        }

        /* Slide down fade-in for menu links */
        .nav-link {
          opacity: 0;
          transform: translateY(-20px);
          animation-fill-mode: forwards;
          animation-name: slideFadeIn;
          animation-duration: 0.6s;
          animation-timing-function: ease-out;
        }
        @keyframes slideFadeIn {
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        /* Stagger animation delays for nav items */
        .nav-link:nth-child(1) { animation-delay: 0.1s; }
        .nav-link:nth-child(2) { animation-delay: 0.2s; }
        .nav-link:nth-child(3) { animation-delay: 0.3s; }
        .nav-link:nth-child(4) { animation-delay: 0.4s; }
        .nav-link:nth-child(5) { animation-delay: 0.5s; }
        .nav-link:nth-child(6) { animation-delay: 0.6s; }
        .nav-link:nth-child(7) { animation-delay: 0.7s; }
        .nav-link:nth-child(8) { animation-delay: 0.8s; }

        /* Active nav link style */
        .active-link {
          color: #38bdf8; /* Tailwind's sky-400 */
          font-weight: 700;
          border-bottom: 2px solid #38bdf8;
          padding-bottom: 2px;
        }

        /* Feature section styles */
        .feature-block {
          display: flex;
          align-items: center;
          background-color: rgba(0,0,0,0.6);
          border-radius: 12px;
          padding: 20px;
          margin-bottom: 2rem;
          gap: 2rem;
          flex-wrap: wrap;
        }
        .feature-block img {
          width: 350px;
          height: 230px;
          object-fit: cover;
          border-radius: 12px;
          flex-shrink: 0;
        }
        .feature-content {
          flex: 1;
          color: white;
        }
        .feature-content h3 {
          font-size: 1.5rem;
          font-weight: 700;
          margin-bottom: 0.75rem;
        }
        .feature-content p {
          font-size: 1.1rem;
          margin-bottom: 1rem;
          line-height: 1.4;
        }
        .btn-get-started {
          background-color: #3b82f6; /* Tailwind blue-500 */
          color: white;
          padding: 10px 18px;
          border-radius: 8px;
          font-weight: 600;
          cursor: pointer;
          border: none;
          transition: background-color 0.3s ease;
          font-size: 1rem;
          user-select: none;
        }
        .btn-get-started:hover {
          background-color: #2563eb; /* Tailwind blue-600 */
        }

        /* Alternate feature layout */
        .feature-left {
          flex-direction: row;
        }
        .feature-right {
          flex-direction: row-reverse;
        }

        /* Responsive */
        @media (max-width: 768px) {
          .feature-block {
            flex-direction: column !important;
            text-align: center;
          }
          .feature-block img {
            width: 100%;
            height: 200px;
          }
          .feature-content {
            padding: 0 1rem;
          }
        }
      `}</style>

      <div className="relative min-h-screen flex flex-col text-white">
        {/* Background Image with overlay */}
        <div
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage:
              "url('https://images.unsplash.com/photo-1498050108023-c5249f4df085?auto=format&fit=crop&w=1470&q=80')",
            zIndex: 0,
          }}
        />
        <div
          className="absolute inset-0 bg-black bg-opacity-70"
          style={{ zIndex: 5 }}
        />

        {/* Navbar */}
        <nav className="flex justify-between items-center py-4 px-6">
          <h2 className="text-xl font-extrabold rainbow-text select-none">
            BLOGGING PLATFORM
          </h2>

          {/* Toggle Button */}
          <button
            className="text-2xl md:hidden"
            onClick={() => setMenuOpen(!menuOpen)}
            aria-label="Toggle Menu"
            style={{ zIndex: 25 }}
          >
            {menuOpen ? "✕" : "☰"}
          </button>

          {/* Nav Links */}
          <ul
            className={`${
              menuOpen ? "flex" : "hidden"
            } flex-col absolute top-full left-0 w-full bg-black bg-opacity-85 shadow-md py-4 px-6 gap-4 font-medium
            md:flex md:flex-row md:static md:shadow-none md:py-0 md:px-0 md:gap-6 md:bg-transparent`}
            style={{ zIndex: 25 }}
          >
            {[
              { to: "", label: "Home" },
              { to: "create-post", label: "Create Blogs" },
              { to: "my-posts", label: "My Blogs" },
              { to: "edit-posts", label: "Edit Blogs" },
              { to: "profile", label: "Profile" },
              { to: "contact", label: "Contact" },
              { to: "about", label: "About" },
            ].map(({ to, label }, idx) => (
              <li
                key={label}
                className="nav-link"
                style={{ animationDelay: `${(idx + 1) * 0.1}s` }}
              >
                <Link
                  to={to}
                  onClick={() => setMenuOpen(false)}
                  className={`hover:text-blue-300 ${
                    isActive(to) ? "active-link" : ""
                  }`}
                >
                  {label}
                </Link>
              </li>
            ))}
            <li className="nav-link" style={{ animationDelay: "0.9s" }}>
              <button
                onClick={handleLogout}
                className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
              >
                Logout
              </button>
            </li>
          </ul>
        </nav>

        {/* Main Content */}
        <main className="flex-grow p-6 relative z-10 max-w-7xl mx-auto">
          <Outlet />

          {/* Three feature blocks */}
          <section className="mt-12">
            {/* 1. Image Left, Content Right */}
            <div className="feature-block feature-left">
              <img
                src="https://images.unsplash.com/photo-1515377905703-c4788e51af15?auto=format&fit=crop&w=600&q=80"
                alt="Write & Share"
              />
              <div className="feature-content">
                <h3>Write & Share</h3>
                <p>
                  Create compelling blog posts effortlessly and share your ideas
                  with the world.
                </p>
                <button
                  className="btn-get-started"
                  onClick={() => navigate("create-post")}
                >
                  Get Started
                </button>
              </div>
            </div>

            {/* 2. Image Right, Content Left */}
            <div className="feature-block feature-right">
              <img
                src="https://images.unsplash.com/photo-1492724441997-5dc865305da7?auto=format&fit=crop&w=600&q=80"
                alt="Engage Audience"
              />
              <div className="feature-content">
                <h3>Engage Audience</h3>
                <p>
                  Connect with readers and build a loyal community through your
                  unique voice.
                </p>
                <button
                  className="btn-get-started"
                  onClick={() => navigate("create-post")}
                >
                  Get Started
                </button>
              </div>
            </div>

            {/* 3. Image Left, Content Right */}
            <div className="feature-block feature-left">
              <img
                src="https://images.unsplash.com/photo-1504384308090-c894fdcc538d?auto=format&fit=crop&w=600&q=80"
                alt="Grow Your Blog"
              />
              <div className="feature-content">
                <h3>Grow Your Blog</h3>
                <p>
                  Publish regularly, analyze feedback, and watch your blog grow
                  in popularity.
                </p>
              </div>
            </div>
            <button
              className="text-center btn-get-started"
              onClick={() => navigate("create-post")}
            >
              Get Started
            </button>
          </section>
        </main>
      </div>
    </>
  );
};

export default UserDashboard;
