import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

export default function Navbar() {
  const [menuOpen, setMenuOpen] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  const closeMenu = () => setMenuOpen(false);

  const isActive = (path) =>
    location.pathname.endsWith(path) ||
    location.pathname === `/user/dashboard/${path}`;

  const handleLogout = () => {
    // Implement your logout logic here
    // For now, just navigate to login page
    navigate("/login");
  };

  return (
    <nav className="bg-black bg-opacity-80 text-white px-6 py-4 flex items-center justify-between relative z-30">
      {/* Logo */}
      <div className="text-2xl font-bold cursor-pointer">
        <Link to="/user/dashboard/home" onClick={closeMenu}>
          MyBlogApp
        </Link>
      </div>

      {/* Mobile Menu Toggle */}
      <button
        className="md:hidden text-white focus:outline-none"
        onClick={() => setMenuOpen(!menuOpen)}
        aria-label="Toggle Menu"
      >
        {menuOpen ? (
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M6 18L18 6M6 6l12 12"
            />
          </svg>
        ) : (
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
        )}
      </button>

      {/* Nav Links */}
      <ul
        className={`${
          menuOpen ? "flex" : "hidden"
        } flex-col absolute top-full left-0 w-full bg-black bg-opacity-90 shadow-md py-4 px-6 gap-4 font-medium
          md:flex md:flex-row md:static md:shadow-none md:py-0 md:px-0 md:gap-10 md:bg-transparent`}
        style={{ zIndex: 25 }}
      >
        {[
          { to: "home", label: "Home" },
          { to: "create-post", label: "Create Blogs" },
          { to: "my-posts", label: "My Blogs" },
          { to: "edit-posts", label: "Edit Blogs" },
          { to: "profile", label: "Profile" },
          { to: "contact", label: "Contact" },
          { to: "about", label: "About" },
        ].map(({ to, label }) => (
          <li key={to}>
            <Link
              to={to}
              onClick={closeMenu}
              className={`hover:text-blue-400 transition-colors duration-300 ${
                isActive(to) ? "text-blue-400 font-semibold" : ""
              }`}
            >
              {label}
            </Link>
          </li>
        ))}

        {/* Logout as button */}
        <li>
          <button
            onClick={() => {
              closeMenu();
              handleLogout();
            }}
            className="bg-red-700 hover:bg-red-800 text-white px-4 py-2 rounded transition-colors duration-300"
          >
            Logout
          </button>
        </li>
      </ul>
    </nav>
  );
}
