import React, { useState } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

const UserDashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div
      className="min-h-screen flex flex-col font-montserrat
      bg-gradient-to-r from-blue-400 via-purple-500 to-pink-500
      bg-[length:200%_200%] animate-bgShift
      text-white"
    >
      {/* Navbar */}
      <nav className="flex justify-between items-center px-8 py-5 bg-black bg-opacity-60 backdrop-blur-md shadow-glowPrimary border-b-4 border-blue-500 sticky top-0 z-50">
        <h1
          className="text-4xl font-extrabold tracking-widest uppercase drop-shadow-[0_0_8px_rgba(59,130,246,0.9)]
          animate-flicker"
          style={{ fontFamily: "'Montserrat', sans-serif" }}
        >
          BLOGGING PLATFORM
        </h1>

        {/* Toggle Button */}
        <button
          aria-label="Toggle Menu"
          onClick={() => setMenuOpen(!menuOpen)}
          className="md:hidden text-5xl animate-bounceHard text-blue-400 hover:text-pink-400 transition-colors duration-300"
        >
          {menuOpen ? "✕" : "☰"}
        </button>

        {/* Links */}
        <ul
          className={`
            md:flex md:flex-row md:gap-10 absolute md:static top-full left-0 w-full md:w-auto
            bg-black bg-opacity-70 md:bg-transparent
            overflow-hidden max-h-0 md:max-h-full
            md:overflow-visible
            transition-[max-height] duration-700 ease-in-out
            ${menuOpen ? "max-h-screen py-6 md:py-0" : ""}
            flex flex-col gap-7 md:gap-0 px-10 md:px-0
          `}
        >
          {[
            { to: "", label: "Home" },
            { to: "create-post", label: "Create Blogs" },
            { to: "my-posts", label: "My Blogs" },
            { to: "edit-posts", label: "Edit Blogs" },
            { to: "profile", label: "Profile" },
            { to: "contact", label: "Contact" },
            { to: "about", label: "About" },
          ].map(({ to, label }) => (
            <li
              key={to}
              className="text-2xl md:text-lg font-semibold
              cursor-pointer
              relative
              group
              hover:text-pink-400
              transition-colors duration-300
              animate-slideFadeIn"
            >
              <Link to={to} onClick={() => setMenuOpen(false)}>
                {label}
                <span
                  className="absolute left-0 -bottom-1 w-0 h-1 bg-pink-400 transition-all group-hover:w-full"
                  aria-hidden="true"
                />
              </Link>
            </li>
          ))}

          <li>
            <button
              onClick={handleLogout}
              className="text-white bg-pink-600 hover:bg-pink-700
              px-6 py-2 rounded-xl font-bold tracking-wider
              shadow-glowDanger hover:shadow-[0_0_25px_10px_rgba(239,68,68,0.9)]
              transition-all duration-300
              animate-pulseGlow
              transform hover:scale-105"
            >
              Logout
            </button>
          </li>
        </ul>
      </nav>

      {/* Main content */}
      <main className="flex-grow p-12 md:p-16 text-black bg-white bg-opacity-90 rounded-t-3xl shadow-lg animate-fade-in">
        <Outlet />
      </main>

      {/* Footer */}
      <footer className="py-6 text-center text-black bg-white bg-opacity-90 font-semibold shadow-inner rounded-b-3xl tracking-wider select-none">
        © {new Date().getFullYear()} MyBlog. All rights reserved.
      </footer>
    </div>
  );
};

export default UserDashboard;
