import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";

const Navbar = () => {
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);

  // Function to check if the link is active
  const isActive = (path) =>
    path === ""
      ? location.pathname === "/" ||
        location.pathname === "/user/dashboard/home"
      : location.pathname.includes(path);

  return (
    <>
      <style>{`
        /* Rainbow text animation */
        .rainbow-text {
          background: linear-gradient(90deg, #ff0057, #ff7c00, #ffeb00, #47ff00, #00ffee, #2b65ff, #8000ff);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          animation: rainbow 5s linear infinite;
          background-size: 400%;
        }
        @keyframes rainbow {
          0% { background-position: 0%; }
          100% { background-position: 400%; }
        }
        /* Navigation link animation */
        .nav-link {
          opacity: 0;
          transform: translateY(-10px);
          animation: slideDown 0.5s forwards ease-in-out;
        }
        @keyframes slideDown {
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        /* Active link highlighting */
        .active-link {
          color: #00d8ff;
          font-weight: bold;
          border-bottom: 2px solid #00d8ff;
        }
        /* Margin between links */
        ul li {
          margin: 0 0.5rem;
        }
        /* Link style and hover effect */
        ul li a {
          display: inline-block;
          padding: 0.5rem 0.75rem;
          transition: color 0.3s ease, background-color 0.3s ease;
          border-radius: 4px;
          text-decoration: none;
        }
        ul li a:hover {
          color: #60a5fa; /* Tailwind blue-400 */
          background-color: rgba(96, 165, 250, 0.1);
        }
      `}</style>

      <nav className="fixed w-full top-0 left-0 z-40 flex justify-between items-center py-4 px-6 bg-black bg-opacity-90 h-16 md:h-20">
        <h2 className="text-4xl font-extrabold rainbow-text select-none tracking-wide m-3 p-2 whitespace-nowrap">
          BLOGGING PLATFORM
        </h2>

        {/* Hamburger menu button - visible only on small screens */}
        <button
          className="text-3xl md:hidden focus:outline-none"
          onClick={() => setMenuOpen(!menuOpen)}
          aria-label="Toggle Menu"
        >
          {menuOpen ? "✕" : "☰"}
        </button>

        {/* Navigation Links */}
        <ul
          className={`
            flex flex-col md:flex-row md:items-center md:static
            absolute top-full left-0 w-full bg-black bg-opacity-90 shadow-md
            md:bg-transparent md:shadow-none
            py-4 px-6 md:py-0 md:px-0
            gap-4 md:gap-6
            font-medium
            transition-transform duration-300 ease-in-out
            md:translate-x-0
            ${menuOpen ? "translate-x-0" : "-translate-x-full"}
            md:flex
          `}
          style={{ zIndex: 25 }}
        >
          {[
            { to: "home", label: "Home" },
            { to: "create-post", label: "Create Blogs" },
            { to: "my-posts", label: "My Blogs" },
            { to: "edit-posts", label: "Edit Blogs" },
            { to: "profile", label: "Profile" },
            { to: "contact", label: "Contact" },
            { to: "about", label: "About" },
          ].map(({ to, label }, idx) => (
            <li
              key={label}
              className="nav-link"
              style={{ animationDelay: `${(idx + 1) * 0.1}s` }}
            >
              <Link
                to={`/user/dashboard/${to}`}
                onClick={() => setMenuOpen(false)}
                className={`hover:text-blue-400 transition-colors duration-300 ${
                  isActive(to) ? "active-link" : ""
                }`}
              >
                {label}
              </Link>
            </li>
          ))}
          <li className="nav-link" style={{ animationDelay: "0.9s" }}>
            <Link
              to="logout"
              className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
              onClick={() => setMenuOpen(false)}
            >
              Logout
            </Link>
          </li>
        </ul>
      </nav>
    </>
  );
};

export default Navbar;
