import React, { useState } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
const UserDashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div
      className="min-h-screen flex flex-col
        bg-gradient-to-br from-blue-50 via-white to-blue-100
        text-gray-800"
    >
      {/* Navbar */}
      <nav className="bg-white shadow-md py-4 px-6 flex justify-between items-center relative z-10">
        <h2 className="text-xl font-bold text-blue-600">BLOGGING PLATFORM</h2>

        {/* Toggle Button (Only visible on small screens) */}
        <button
          className="text-2xl md:hidden"
          onClick={() => setMenuOpen(!menuOpen)}
          aria-label="Toggle Menu"
        >
          {menuOpen ? "✕" : "☰"}
        </button>

        {/* Nav Links */}
        <ul
          className={`${
            menuOpen ? "flex" : "hidden"
          } flex-col absolute top-full left-0 w-full bg-white shadow-md py-4 px-6 gap-4 text-gray-700 font-medium
          md:flex md:flex-row md:static md:shadow-none md:py-0 md:px-0 md:gap-6`}
        >
          <li>
            <Link
              to=""
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-500"
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="create-post"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-500"
            >
              Create Blogs
            </Link>
          </li>
          <li>
            <Link
              to="my-posts"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-500"
            >
              My Blogs
            </Link>
          </li>
          <li>
            <Link
              to="edit-posts"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-500"
            >
              Edit Blogs
            </Link>
          </li>
          <li>
            <Link
              to="profile"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-500"
            >
              Profile
            </Link>
          </li>
          <li>
            <Link
              to="contact"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-500"
            >
              Contact
            </Link>
          </li>
          <li>
            <Link
              to="about"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-500"
            >
              About
            </Link>
          </li>
          <li>
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
            >
              Logout
            </button>
          </li>
        </ul>
      </nav>

      {/* Main Content */}
      <main className="flex-grow p-6 relative z-10">
        <Outlet />
      </main>

      {/* Footer */}
      <footer className="bg-white shadow-inner p-4 text-center text-gray-600 relative z-10">
        © {new Date().getFullYear()} MyBlog. All rights reserved.
      </footer>
    </div>
  );
};

export default UserDashboard;
