import React, { useState } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

const UserDashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div className="relative min-h-screen flex flex-col text-white">
      {/* Background Image */}
      <div
        className="absolute inset-0 bg-cover bg-center"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=1470&q=80')",
          zIndex: 0,
        }}
      />
      {/* Overlay */}
      <div
        className="absolute inset-0 bg-black bg-opacity-50"
        style={{ zIndex: 1 }}
      />

      {/* Content */}
      <nav className="relative z-10 bg-transparent shadow-md py-4 px-6 flex justify-between items-center">
        <h2 className="text-xl bg-black text-white font-extrabold">
          BLOGGING PLATFORM
        </h2>

        {/* Toggle Button (Only visible on small screens) */}
        <button
          className="text-2xl md:hidden"
          onClick={() => setMenuOpen(!menuOpen)}
          aria-label="Toggle Menu"
        >
          {menuOpen ? "✕" : "☰"}
        </button>

        {/* Nav Links */}
        <ul
          className={`${
            menuOpen ? "flex" : "hidden"
          } flex-col absolute top-full left-0 w-full bg-black bg-opacity-70 shadow-md py-4 px-6 gap-4 font-medium
          md:flex md:flex-row md:static md:shadow-none md:py-0 md:px-0 md:gap-6 md:bg-transparent`}
        >
          <li>
            <Link
              to=""
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-300"
            >
              Home
            </Link>
          </li>
          <li>
            <Link
              to="create-post"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-300"
            >
              Create Blogs
            </Link>
          </li>
          <li>
            <Link
              to="my-posts"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-300"
            >
              My Blogs
            </Link>
          </li>
          <li>
            <Link
              to="edit-posts"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-300"
            >
              Edit Blogs
            </Link>
          </li>
          <li>
            <Link
              to="profile"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-300"
            >
              Profile
            </Link>
          </li>
          <li>
            <Link
              to="contact"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-300"
            >
              Contact
            </Link>
          </li>
          <li>
            <Link
              to="about"
              onClick={() => setMenuOpen(false)}
              className="hover:text-blue-300"
            >
              About
            </Link>
          </li>
          <li>
            <button
              onClick={handleLogout}
              className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
            >
              Logout
            </button>
          </li>
        </ul>
      </nav>

      <main className="flex-grow p-6 relative z-10">
        <Outlet />
      </main>

      <footer className="relative z-10 bg-black bg-opacity-60 p-4 text-center text-gray-300">
        © {new Date().getFullYear()} MyBlog. All rights reserved.
      </footer>
    </div>
  );
};

export default UserDashboard;
