import React, { useState } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

const UserDashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div
      className="min-h-screen flex flex-col font-montserrat
        animate-bg-shift
        transition-colors duration-1000"
    >
      {/* Navbar */}
      <nav
        className="bg-white shadow-glow-primary py-4 px-6 flex justify-between items-center relative
          border-b-4 border-primary"
      >
        <h2
          className="text-3xl font-extrabold text-primary tracking-wide animate-fade-in"
          style={{ textShadow: "0 0 5px #3b82f6" }}
        >
          BLOGGING PLATFORM
        </h2>

        {/* Toggle Button (Only visible on small screens) */}
        <button
          className="text-4xl md:hidden text-primary hover:text-blue-700 transition-colors duration-300 select-none"
          onClick={() => setMenuOpen(!menuOpen)}
          aria-label="Toggle Menu"
          style={{ filter: "drop-shadow(0 0 2px #3b82f6)" }}
        >
          {menuOpen ? "✕" : "☰"}
        </button>

        {/* Nav Links */}
        <ul
          className={`${
            menuOpen ? "max-h-screen" : "max-h-0"
          } overflow-hidden flex-col absolute top-full left-0 w-full bg-white shadow-lg py-4 px-6 gap-6 text-primary font-semibold
          md:max-h-full md:flex md:flex-row md:static md:shadow-none md:py-0 md:px-0 md:gap-10
          transition-[max-height] duration-500 ease-in-out`}
        >
          {[
            { to: "", label: "Home" },
            { to: "create-post", label: "Create Blogs" },
            { to: "my-posts", label: "My Blogs" },
            { to: "edit-posts", label: "Edit Blogs" },
            { to: "profile", label: "Profile" },
            { to: "contact", label: "Contact" },
            { to: "about", label: "About" },
          ].map(({ to, label }) => (
            <li
              key={to}
              className="relative group cursor-pointer hover:text-primary hover:underline"
              style={{ textShadow: "0 0 3px #3b82f6" }}
            >
              <Link
                to={to}
                onClick={() => setMenuOpen(false)}
                className="transition-colors duration-300"
              >
                {label}
              </Link>
              {/* Animated underline on hover */}
              <span
                className="absolute left-0 bottom-0 h-[2px] w-0 bg-primary transition-all group-hover:w-full"
                aria-hidden="true"
              ></span>
            </li>
          ))}

          <li>
            <button
              onClick={handleLogout}
              className="bg-danger px-5 py-2 rounded-lg font-bold tracking-wider text-white
                hover:bg-red-700 hover:shadow-glow-danger transition-all duration-300
                transform hover:-translate-y-0.5"
            >
              Logout
            </button>
          </li>
        </ul>
      </nav>

      {/* Main Content */}
      <main
        className="flex-grow p-10 text-gray-800 animate-fade-in"
        style={{ textShadow: "0 0 5px rgba(0,0,0,0.1)" }}
      >
        <Outlet />
      </main>

      {/* Footer */}
      <footer
        className="bg-white shadow-inner p-6 text-center text-gray-600 font-semibold
          tracking-wide select-none border-t border-gray-200"
      >
        © {new Date().getFullYear()} MyBlog. All rights reserved.
      </footer>
    </div>
  );
};

export default UserDashboard;
