import React, { useState } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

const UserDashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div className="min-h-screen flex flex-col text-white">
      {/* Header with Background */}
      <nav className="relative z-10 bg-transparent shadow-md py-4 px-6 flex justify-between items-center">
        {/* Header BG Image */}
        <div
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage:
              "url('https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1470&q=80')",
            zIndex: 0,
          }}
        />
        {/* Header Overlay */}
        <div
          className="absolute inset-0 bg-black bg-opacity-60"
          style={{ zIndex: 1 }}
        />

        {/* Header Content */}
        <h2 className="relative z-10 text-xl font-extrabold text-white">
          BLOGGING PLATFORM
        </h2>

        {/* Toggle Button (Only visible on small screens) */}
        <button
          className="relative z-10 text-2xl md:hidden"
          onClick={() => setMenuOpen(!menuOpen)}
          aria-label="Toggle Menu"
        >
          {menuOpen ? "✕" : "☰"}
        </button>

        {/* Nav Links */}
        <ul
          className={`${
            menuOpen ? "flex" : "hidden"
          } flex-col absolute top-full left-0 w-full bg-black bg-opacity-70 shadow-md py-4 px-6 gap-4 font-medium
          md:flex md:flex-row md:static md:shadow-none md:py-0 md:px-0 md:gap-6 md:bg-transparent`}
        >
          {[
            "",
            "create-post",
            "my-posts",
            "edit-posts",
            "profile",
            "contact",
            "about",
          ].map((path, idx) => (
            <li key={idx}>
              <Link
                to={path}
                onClick={() => setMenuOpen(false)}
                className="hover:text-blue-300"
              >
                {path === ""
                  ? "Home"
                  : path
                      .replace("-", " ")
                      .replace(/\b\w/g, (c) => c.toUpperCase())}
              </Link>
            </li>
          ))}
          <li>
            <button
              onClick={handleLogout}
              className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded relative z-10"
            >
              Logout
            </button>
          </li>
        </ul>
      </nav>

      {/* Main Content with Background */}
      <main className="relative flex-grow p-6">
        {/* Main BG Image */}
        <div
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage:
              "url('https://images.unsplash.com/photo-1494526585095-c41746248156?auto=format&fit=crop&w=1470&q=80')",
            zIndex: 0,
          }}
        />
        {/* Main Overlay */}
        <div
          className="absolute inset-0 bg-black bg-opacity-50"
          style={{ zIndex: 1 }}
        />

        {/* Main Content */}
        <div className="relative z-10">
          <Outlet />
        </div>
      </main>

      {/* Footer with Background */}
      <footer className="relative z-10 p-4 text-center text-gray-300">
        {/* Footer BG Image */}
        <div
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage:
              "url('https://images.unsplash.com/photo-1500534623283-312aade485b7?auto=format&fit=crop&w=1470&q=80')",
            zIndex: 0,
          }}
        />
        {/* Footer Overlay */}
        <div
          className="absolute inset-0 bg-black bg-opacity-60"
          style={{ zIndex: 1 }}
        />

        {/* Footer Content */}
        <div className="relative z-10">
          © {new Date().getFullYear()} MyBlog. All rights reserved.
        </div>
      </footer>
    </div>
  );
};

export default UserDashboard;
