import React, { useState } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

const UserDashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <>
      <style>{`
        /* Rainbow gradient animated text */
        @keyframes rainbow {
          0%, 100% {
            background-position: 0% 50%;
          }
          50% {
            background-position: 100% 50%;
          }
        }
        .rainbow-text {
          background: linear-gradient(270deg, #ff0000, #ffa500, #ffff00, #00ff00, #00ffff, #0000ff, #ff00ff, #ff0000);
          background-size: 1600% 1600%;
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          animation: rainbow 8s ease infinite;
        }

        /* Slide down fade-in for menu links */
        .nav-link {
          opacity: 0;
          transform: translateY(-20px);
          animation-fill-mode: forwards;
          animation-name: slideFadeIn;
          animation-duration: 0.6s;
          animation-timing-function: ease-out;
        }
        @keyframes slideFadeIn {
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        /* Stagger animation delays for nav items */
        .nav-link:nth-child(1) { animation-delay: 0.1s; }
        .nav-link:nth-child(2) { animation-delay: 0.2s; }
        .nav-link:nth-child(3) { animation-delay: 0.3s; }
        .nav-link:nth-child(4) { animation-delay: 0.4s; }
        .nav-link:nth-child(5) { animation-delay: 0.5s; }
        .nav-link:nth-child(6) { animation-delay: 0.6s; }
        .nav-link:nth-child(7) { animation-delay: 0.7s; }
        .nav-link:nth-child(8) { animation-delay: 0.8s; }
      `}</style>

      <div className="relative min-h-screen flex flex-col text-white">
        {/* Background Image with overlay */}
        <div
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage:
              "url('https://images.unsplash.com/photo-1498050108023-c5249f4df085?auto=format&fit=crop&w=1470&q=80')",
            zIndex: 0,
          }}
        />
        <div
          className="absolute inset-0 bg-black bg-opacity-70"
          style={{ zIndex: 1 }}
        />

        {/* Navbar */}
        <nav className="relative z-10 flex justify-between items-center py-4 px-6 bg-transparent shadow-md">
          <h2 className="text-xl font-extrabold rainbow-text select-none">
            BLOGGING PLATFORM
          </h2>

          {/* Toggle Button */}
          <button
            className="text-2xl md:hidden"
            onClick={() => setMenuOpen(!menuOpen)}
            aria-label="Toggle Menu"
          >
            {menuOpen ? "✕" : "☰"}
          </button>

          {/* Nav Links */}
          <ul
            className={`${
              menuOpen ? "flex" : "hidden"
            } flex-col absolute top-full left-0 w-full bg-black bg-opacity-70 shadow-md py-4 px-6 gap-4 font-medium
            md:flex md:flex-row md:static md:shadow-none md:py-0 md:px-0 md:gap-6 md:bg-transparent`}
          >
            {[
              { to: "", label: "Home" },
              { to: "create-post", label: "Create Blogs" },
              { to: "my-posts", label: "My Blogs" },
              { to: "edit-posts", label: "Edit Blogs" },
              { to: "profile", label: "Profile" },
              { to: "contact", label: "Contact" },
              { to: "about", label: "About" },
            ].map(({ to, label }, idx) => (
              <li
                key={label}
                className="nav-link"
                style={{ animationDelay: `${(idx + 1) * 0.1}s` }}
              >
                <Link
                  to={to}
                  onClick={() => setMenuOpen(false)}
                  className="hover:text-blue-300"
                >
                  {label}
                </Link>
              </li>
            ))}
            <li className="nav-link" style={{ animationDelay: "0.9s" }}>
              <button
                onClick={handleLogout}
                className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
              >
                Logout
              </button>
            </li>
          </ul>
        </nav>

        {/* Main Content */}
        <main className="flex-grow p-6 relative z-10">
          <Outlet />

          {/* Three feature blocks with images and text */}
          <section className="mt-12 grid grid-cols-1 md:grid-cols-3 gap-8">
            {/* Block 1 */}
            <div className="bg-black bg-opacity-60 rounded-lg p-4 flex flex-col items-center text-center shadow-lg">
              <img
                src="https://images.unsplash.com/photo-1515377905703-c4788e51af15?auto=format&fit=crop&w=400&q=80"
                alt="Write & Share"
                className="rounded-md mb-4 w-full h-48 object-cover"
              />
              <h3 className="text-lg font-bold mb-2">Write & Share</h3>
              <p>
                Create compelling blog posts effortlessly and share your ideas
                with the world.
              </p>
            </div>

            {/* Block 2 */}
            <div className="bg-black bg-opacity-60 rounded-lg p-4 flex flex-col items-center text-center shadow-lg">
              <img
                src="https://images.unsplash.com/photo-1492724441997-5dc865305da7?auto=format&fit=crop&w=400&q=80"
                alt="Engage Audience"
                className="rounded-md mb-4 w-full h-48 object-cover"
              />
              <h3 className="text-lg font-bold mb-2">Engage Your Audience</h3>
              <p>
                Interact with readers via comments, likes, and personalized
                content tailored for your followers.
              </p>
            </div>

            {/* Block 3 */}
            <div className="bg-black bg-opacity-60 rounded-lg p-4 flex flex-col items-center text-center shadow-lg">
              <img
                src="https://images.unsplash.com/photo-1504384308090-c894fdcc538d?auto=format&fit=crop&w=400&q=80"
                alt="Grow Your Presence"
                className="rounded-md mb-4 w-full h-48 object-cover"
              />
              <h3 className="text-lg font-bold mb-2">Grow Your Presence</h3>
              <p>
                Expand your reach and build a professional blogging brand with
                easy tools and analytics.
              </p>
            </div>
          </section>
        </main>

        {/* Footer */}
        <footer className="relative z-20 bg-black bg-opacity-50 p-4 text-center text-white font-semibold">
          © {new Date().getFullYear()} MyBlog. All rights reserved.
        </footer>
      </div>
    </>
  );
};

export default UserDashboard;
