import React from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";

const UserDashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      {/* Navbar */}
      <nav className="bg-white shadow-md py-4 px-6 flex justify-between items-center">
        <h2 className="text-xl font-bold text-blue-600">BLO</h2>
        <ul className="flex flex-wrap gap-4 items-center text-gray-700 font-medium">
          <li>
            <Link to="" className="hover:text-blue-500">
              Home
            </Link>
          </li>
          <li>
            <Link to="create-post" className="hover:text-blue-500">
              Create Blogs
            </Link>
          </li>
          <li>
            <Link to="my-posts" className="hover:text-blue-500">
              My Blogs
            </Link>
          </li>
          <li>
            <Link to="profile" className="hover:text-blue-500">
              Profile
            </Link>
          </li>
          <li>
            <Link to="contact" className="hover:text-blue-500">
              Contact
            </Link>
          </li>
          <li>
            <Link to="about" className="hover:text-blue-500">
              About
            </Link>
          </li>
          <li>
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
            >
              Logout
            </button>
          </li>
        </ul>
      </nav>

      {/* Main Content */}
      <main className="flex-grow p-6">
        <Outlet />
      </main>

      {/* Footer */}
      <footer className="bg-white shadow-inner p-4 text-center text-gray-600">
        Â© {new Date().getFullYear()} MyBlog. All rights reserved.
      </footer>
    </div>
  );
};

export default UserDashboard;
